package com.biz.if_then;

public class If_04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 1부터 100까지의 숫자중에 6의 배수 찾기
		for(int i =1 ; i<100 ; i++){
			//i를 6으로 나누었을때 나머지가 0인 수 (6의배수)
			if( i % 6 == 0 ) {
				System.out.println(i + " 는 6의 배수입니다 ");
			}
		}
		
		
		
		
		
		
		
		
		
		
		
	}

}
